import './App.css';
import React,{Component} from "react";
import libraryService from "../../libraryRepository/libraryService";
import {BrowserRouter as Router, Routes, Route } from "react-router-dom";
import BookList from "../Books/BookList/bookList";
import Authors from "../Authors/authors";
import Header from "../Header/header";


class App extends Component{


  constructor(props) {
    super(props);
    this.state={
      books: [],
      authors:[]
    }
  }

  render() {
    return(
        <Router>
            <Header/>
          <main>
            <div className="container">
              <Routes>
                <Route path={"/books"} exact element={<BookList books={this.state.books}/>}/>
                <Route path={"/"} exact element={<BookList books={this.state.books}/>}/>
                <Route path={"/authors"} exact element={<Authors authors={this.state.authors}/>}/>
              </Routes>
            </div>
          </main>
        </Router>
    );
  }

  componentDidMount() {
    this.loadBooks();
    this.loadAuthors();
  }

  loadBooks=() => {
    libraryService.fetchBooks().then(
        (data) => {
          this.setState({
            books: data.data
          })
        });
  }

  loadAuthors=() => {
      libraryService.fetchAuthors().then(
          (data) => {
              this.setState({
                  authors: data.data
              })
          });
    }


}

export default App;
