import React from "react";
import {useNavigate} from 'react-router-dom';

const BookAdd=(props) => {

    const history=useNavigate();

    const[formData, updateFromData]=React.useState({
        name:"",
        category:0,
        author:0,
        copies:0
    })

    const handleChange = (e) => {
        updateFromData({
            ...formData,
            [e.target.name]: e.target.value.trim()
        })
    }

    const onFormSubmit = (event) => {
        event.preventDefault();

        const name = formData.name;
        const category = formData.category;
        const author = formData.author;
        const copies = formData.copies;

        props.onAdd(name,price,quantity,category,manufacturer);
        history("/products");
    }

    return(
        <div className="row mt-5">
            <div className="col-md-5">
                <form onSubmit={onFormSubmit}>
                    <div className="form-group">
                        <label htmlFor="name">Product name</label>
                        <input type="text" placeholder="Enter product name" id="name" name="name" required
                               className="form-control" onChange={handleChange}
                        />
                        <label htmlFor="price">Price</label>
                        <input type="text" placeholder="Enter product price" id="price" name="price"
                               className="form-control" required onChange={handleChange}
                        />
                        <label htmlFor="quantity">Quantity</label>
                        <input type="text" placeholder="Enter product quantity" id="quantity" name="quantity"
                               className="form-control" required onChange={handleChange}
                        />
                    </div>
                    <div className="form-group">
                        <label>Category</label>
                        <select name="category" className="form-group" required onChange={handleChange} style={{marginTop: '10px', marginLeft: '10px'}}>
                            {props.categories.map((term) =>
                                <option value={term.id}>{term.name}</option>
                            )}
                        </select>
                    </div>
                    <div className="form-group">
                        <label>Manufacturer</label>
                        <select name="manufacturer" className="form-group" required onChange={handleChange} style={{marginTop: '10px', marginLeft: '10px'}} >
                            {props.manufacturers.map((term) =>
                                <option value={term.id}>{term.name}</option>
                            )}
                        </select>
                    </div>
                    <button type="submit" className="btn btn-primary" id="submit" style={{marginTop: '10px'}}>Submit</button>
                </form>
            </div>
        </div>
    )
}

export default BookAdd;
