import './App.css';
import React,{Component} from "react";
import EshopService from "../../repository/eshopRepository";
import {BrowserRouter as Router, Routes, Route } from "react-router-dom";
import BookList from "../Books/BookList/bookList";


class App extends Component{


  constructor(props) {
    super(props);
    this.state={
      books: []
    }
  }

  render() {
    return(
        <Router>
          <Header/>
          <main>
            <div className="container">
              <Routes>
                <Route path={"/books"} exact element={<BookList books={this.state.books}/>}/>

              </Routes>
            </div>
          </main>
        </Router>
    );
  }

  componentDidMount() {
    this.loadManufacturers();
    this.loadCategories();
    this.loadProducts();
  }

  loadManufacturers=() => {
    EshopService.fetchManufacturers().then(
        (data) => {
          this.setState({
            manufacturers: data.data
          })
        });
  }

  loadCategories=() => {
    EshopService.fetchCategories().then(
        (data) => {
          this.setState({
            categories: data.data
          })
        });
  }

  loadProducts=() => {
    EshopService.fetchProducts().then(
        (data) => {
          this.setState({
            products: data.data
          })
        });
  }

  deleteProduct=(id) => {
    EshopService.deleteProduct(id).then(() => {
      this.loadProducts();
    })
  }

  addProduct=(name, price, quantity, category, manufacturer) => {
    EshopService.addProduct(name, price, quantity, category, manufacturer).then(() => {
      this.loadProducts();
    })
  }

  getProduct=(id) => {
    EshopService.getProduct(id).then((data) => {
      this.setState({
        selectedProduct: data.data
      })
    })
  }

  editProduct=(id, name, price, quantity, category, manufacturer) => {
    EshopService.editProduct(id, name, price, quantity, category, manufacturer).then(() => {
      this.loadProducts();
    })
  }
}

export default App;
